import Symbol from './Symbol.js';
import { msr } from '../services/regExpService.js';

export default class ColorlessManaSymbol extends Symbol {
    static match(str) {
        return str.match(msr`C`);
    }

    get colors() {
        return [];
    }

    get type() {
        return 'colorlessMana';
    }

    cmcValue() {
        return 1;
    }
}
